import React, { Component } from "react";
import Slideshow from "./Slideshow";
import slide1 from "./assets/slide1.jpg";
import slide2 from "./assets/slide2.jpg";
import slide3 from "./assets/slide3.jpg";
import slide4 from "./assets/slide4.jpg";
import slide5 from "./assets/slide5.jpg";
import classes from "./AdSlider.module.css";

class AdSlider extends Component {

    render() {
        return (
            <div className={s.container}>
                <div className={s.header}>Automatic Slideshow Carousel</div>
                <div className={s.main}>
                    <Slideshow slides={slides} />
                </div>
                <div className={s.footer}>Built in React - by Ashwanth A R</div>
            </div>
        );
    }
}

const s = {
    container: "fullW fullH rel overflowH",
    onScreen: "left0",
    offScreenRight: "left100vw",
    offScreenLeft: "leftM100vw",
    transition: "transition1l"
};

export default AdSlider;
