import React, { Component } from "react";
import { Link } from "react-router-dom";
import classes from './SignIn.module.css';

export default class Login extends Component {
    state={
        email:"",
        password:"",
        emailError:"",
        passwordError:"",
    }

    handlechange = (event) =>{
        this.setState({
            [event.target.name]:
            event.target.value,
        })
    }

    validate = () =>{
        let emailError = "";
        let passwordError = "";

        if(!this.state.email) {
            emailError = "please enter your email";
        }

        if(!this.state.password) {
            passwordError = "please "
        }
    }

    render() {
        return (

            <>
            <div className={classes.signinWrapper}>
             <form className={classes.signinForm}>
             <h2>Sign In</h2>
             <label>Email address:</label><br/>
             <input type="email" className={classes.formControl} placeholder="Enter email" /><br/><br/>

             <label>Password:</label><br/>
             <input type="password" className={classes.formControl} placeholder="Enter password" /><br/>

             <input type="checkbox" className={classes.customControlInput} id="customCheck1" />
             <label className={classes.customControlLabel} htmlFor="customCheck1">Remember me</label>
             <br/><br/>

             <button type="submit" className={classes.btn}>Submit</button>
             <p className="forgot-password text-right">
                 <p>If you are new to the Application, <Link to="/sign-up">Sign up here</Link></p>
              Forgot <a href="#">password?</a></p>
             </form>
            </div>
            </>

        );
    }
}
