import React, { Component } from "react";
import classes from "./SupplierCode.module.css";
import { FormControl } from "react-bootstrap";
import { Button } from "react-bootstrap";
<link rel="stylesheet" type="text/css" href="your_website_domain/css_root/flaticon.css"/>

// import styles from "./SupplierCode.less";

class SupplierCode extends Component {
	state = {
		type: "password",
	};

	showHide = (e) => {
		e.preventDefault();
		e.stopPropagation();
		this.setState({
			type: this.state.type === "text" ? "password" : "text",
		});
		this.input.focus();
	};

	render() {
		return (
			<>
				<div className={classes.h3Field}>
					<h3 className={classes.h3}>Enter Supplier Code</h3>
					<div className={classes.passVisible}>
						<FormControl
							type={this.state.type}
							{...this.props}
							inputRef={(ref) => {
								this.input = ref;
							}}
						/>

						<span
							className={[
								classes.visibility,
								"flaticon-eye",
								this.state.type === "text"
									? classes.is_visible
									: classes.is_hidden,
							].join(" ")}
							onClick={this.showHide}
						></span>
						<br />
						<Button variant="primary" className={classes.btn}>
							Submit
						</Button>
					</div>
				</div>
			</>
		);
	}
}

export default SupplierCode;
