import React, { Component } from "react";
import classes from "./SupplierCode.module.css";
import {FormControl} from 'react-bootstrap';
import styles from './PasswordField.less';

class SupplierCode extends Component {
    state = {
        type: 'password'
  };

  showHide = (e) => {
      e.preventDefault();
      e.stopPropagation();
      this.setState({
          type: this.state.type === 'text' ? 'password' : 'text'
      });
      this.input.focus();
  }


    render() {
        return (
            <>
			<div className={classes.enterCode}>
			<h3>Enter Supplier Code</h3><br/>
            <input type="text"/>
			</div>

            </>
        );
    }
}

export default SupplierCode;
