import PropTypes from "prop-types";
import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import {
    Button,
    Container,
    Icon,
    Menu,
    Responsive,
    Segment,
    Sidebar,
    Visibility,
    Dropdown,
} from "semantic-ui-react";
import styles from "../../page.module.scss";


const options = [
    { key: 'logout', icon: 'logout', text: 'Logout', value: 'Logout' }, { key: 'profile', icon: 'profile', text: 'Profile', value: 'Profile' }]
const getWidth = () => {
    const isSSR = typeof window == "undefined";
    return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth;
};

class DesktopContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            activeItem: "/",
        };
    }

    componentDidMount(){

     this.setState({
            activeItem:  this.props.location.pathname
        })
    }
    onChange=(event, data)=>{
        debugger;
        console.log(event, data)

        if(data.value == "Logout") {
            this.props.handleLogout()
        }
        else if (data.value=='Profile'){
            this.props.history.push('/profile')
        }
        }


    hideFixedMenu = () => this.setState({ fixed: false });
    showFixedMenu = () => this.setState({ fixed: true });

    handleItemClick = (e, { name }) => this.setState({ activeItem: name });
    render() {
        const { children } = this.props;
        const { fixed } = this.state;
        const { activeItem } = this.state;

        return (
            <Responsive
                getWidth={getWidth}
                minWidth={Responsive.onlyTablet.minWidth}
            >
                <Visibility
                    once={false}
                    onBottomPassed={this.showFixedMenu}
                    onBottomPassedReverse={this.hideFixedMenu}
                >
                    <Segment
                        inverted
                        textAlign="center"
                        style={{ height: 100, padding: "1em 0em" }}
                        vertical
                    >
                        <Menu
                            fixed={fixed ? "top" : null}
                            inverted={!fixed}
                            pointing={!fixed}
                            secondary={!fixed}
                            size="large"
                        >
                            <Container>
                                {this.props.location.pathname !== "/" ? (
                                    <>
                                        <Menu.Item
                                            as={Link}
                                            to="/"
                                            name="/"
                                            active={
                                                activeItem == "/" ||
                                                activeItem == "/"
                                            }
                                            onClick={this.handleItemClick}
                                        >
                                            Home
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/themes"
                                            name="/themes"
                                            active={activeItem == "/themes"}
                                            onClick={this.handleItemClick}
                                        >
                                            Themes
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/plugins"
                                            name="/plugins"
                                            active={activeItem == "/plugins"}
                                            onClick={this.handleItemClick}
                                        >
                                            Plugins
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/membership"
                                            name="/membership"
                                            active={activeItem == "/membership"}
                                            onClick={this.handleItemClick}
                                        >
                                            Membership
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/ngoform"
                                            name="/ngoform"
                                            active={activeItem == "/ngoform"}
                                            onClick={this.handleItemClick}
                                        >
                                            NGO Form
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/contactus"
                                            name="/contactus"
                                            active={activeItem == "/contactus"}
                                            onClick={this.handleItemClick}
                                        >
                                            Contact Us
                                        </Menu.Item>
                                    </>
                                ) : (
                                    <>
                                        <Menu.Item
                                            as={Link}
                                            to="/"
                                            name="/"
                                            active={
                                                activeItem == "/" ||
                                                activeItem == "logo"
                                            }
                                            onClick={this.handleItemClick}
                                        >
                                            Home
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/membership"
                                            name="/membership"
                                            active={activeItem == "/membership"}
                                            onClick={this.handleItemClick}
                                        >
                                            Membership
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/ngoform"
                                            name="/ngoform"
                                            active={activeItem == "/ngoform"}
                                            onClick={this.handleItemClick}
                                        >
                                            NGO Form
                                        </Menu.Item>
                                        <Menu.Item
                                            as={Link}
                                            to="/contactus"
                                            name="/contactus"
                                            active={activeItem == "/contactus"}
                                            onClick={this.handleItemClick}
                                        >
                                            Contact Us
                                        </Menu.Item>
                                    </>
                                )}

