import React, { Component } from "react";
import classes from "./SupplierCode.module.css";
import { FormControl } from "react-bootstrap";
// import styles from "./SupplierCode.less";


class SupplierCode extends Component {
	state = {
		type: "password",
	};

	showHide = (e) => {
		e.preventDefault();
		e.stopPropagation();
		this.setState({
			type: this.state.type === "text" ? "password" : "text",
		});
		this.input.focus();
	};

	render() {
		return (
            <>
            <h3 className={cla}>Enter Supplier Code</h3>
			<div className={classes.passVisible}>
				<FormControl
					type={this.state.type}
					{...this.props}
					inputRef={(ref) => {
						this.input = ref;
					}}
				/>

				<span 
					className={[
						classes.visibility,
						"flaticon-eye",
						this.state.type === "text" ? classes.is_visible : classes.is_hidden,
					].join(" ")}
					onClick={this.showHide}
				></span>

			</div>
            </>
		);
	}
}

export default SupplierCode;
